pragma solidity 0.8.10;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IOFTReceiverV2.sol";
import "./interfaces/IOFTV2.sol";
import "./interfaces/IOFTWithFee.sol";
import "./interfaces/XCM.sol";
import "./interfaces/XCM_v2.sol";
import "./utils/BuildCallData.sol";
import "./utils/AddressToAccount.sol";
import "./AstarSlpx.sol";


contract AstarReceiver is Ownable, IOFTReceiverV2 {
    bytes1 private constant ASTAR_CHAIN_TYPE = 0x00;
    bytes2 private constant ASTR_CURRENCY_ID = 0x0803;
    bytes2 private constant VASTR_CURRENCY_ID = 0x0903;
    uint256 private constant BIFROST_PARA_ID = 2030;
    bool private constant IS_RELAY_CHAIN = false;
    address public VASTR = 0xfffFffff00000000000000010000000000000010;
    address public BNC = 0xfFffFffF00000000000000010000000000000007;
    address public astarSlpx = 0x2fD8bbF5dc8b342C09ABF34f211b3488e2d9d691;
    address public polkadotXcm = 0x0000000000000000000000000000000000005004;
    address public astrNativeOFT = 0xEaFAF3EDA029A62bCbE8a0C9a4549ef0fEd5a400;
    address public vAstrProxyOFT = 0xF1d4797E51a4640a76769A50b57abE7479ADd3d8;
    address public astarZkSlpx;
    uint16  public destChainId = 10220;
    mapping(address => address) public derivativeAddress;

    event Mint(address caller, address derivativeAddress, uint256 amount);
    event Redeem(address caller, address derivativeAddress, uint256 amount);

    constructor(address _astarZkSlpx) {
       astarZkSlpx = _astarZkSlpx;
    }

    function zkSlpxMint(address _from, address _to, uint256 _amount) internal {
        require(_from != address(0), "Mint: _from != address(0)");
        require(_to != address(0), "Mint: _to != address(0)");
        require(_amount != 0, "Mint: _amount != 0");
        xcmTransferNativeAsset(_from, _amount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN_TYPE, _to);
        bytes memory callData = BuildCallData.buildMintCallBytes(_from, ASTR_CURRENCY_ID, targetChain, "AstarZkEvm");
        (uint64 transactWeight, uint256 feeAmount) = AstarSlpx(astarSlpx).operationToFeeInfo(AstarSlpx.Operation.Mint);

        // xcm transact
        require(XCM(polkadotXcm).remote_transact(BIFROST_PARA_ID,IS_RELAY_CHAIN,BNC,feeAmount,callData,transactWeight),"Failed to send xcm");
        emit Mint(_from, _to, _amount);
    }

    function zkSlpxRedeem(address _from, address _to, uint256 _amount) internal {
        require(_from != address(0), "Redeem: _from != address(0)");
        require(_to != address(0), "Redeem: _to != address(0)");
        require(_amount != 0, "Redeem: _amount != 0");
        xcmTransferAsset(VASTR, _from, _amount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN_TYPE, _to);
        bytes memory callData = BuildCallData.buildRedeemCallBytes(_from, VASTR_CURRENCY_ID, targetChain);
        (uint64 transactWeight, uint256 feeAmount) = AstarSlpx(astarSlpx).operationToFeeInfo(AstarSlpx.Operation.Redeem);

        // xcm transact
        require(XCM(polkadotXcm).remote_transact(BIFROST_PARA_ID,IS_RELAY_CHAIN,BNC,feeAmount,callData,transactWeight),"Failed to send xcm");
        emit Mint(_from, _to, _amount);
    }


    function onOFTReceived(uint16, bytes calldata, uint64, bytes32 _from, uint _amount, bytes calldata _payload) external override {
        require(_msgSender() == astrNativeOFT || _msgSender() == vAstrProxyOFT, "only native oft can call");
        require(address(uint160(uint(_from))) == astarZkSlpx, "only receive msg from astarZkSlpx");
        (address caller, uint8 actionId) = abi.decode(_payload, (address, uint8));
        if(derivativeAddress[caller] == address(0)) {
            setDerivativeAddress(caller);
        }

        if(actionId == 0) {
            IOFTWithFee(astrNativeOFT).withdraw(_amount);
            zkSlpxMint(caller, derivativeAddress[caller], _amount);
        } else if (actionId == 1) {
            zkSlpxRedeem(caller, derivativeAddress[caller], _amount);
        }
    }

    function claimVAstr(address addr, bytes calldata _adapterParams) external payable {
        uint256 amount = DerivativeContract(payable(derivativeAddress[addr])).withdrawVAstr();
        IERC20(VASTR).approve(vAstrProxyOFT, amount);
        ICommonOFT.LzCallParams memory callParams = ICommonOFT.LzCallParams(
            payable(_msgSender()),
            address(0),
            _adapterParams
        );
        IOFTV2(vAstrProxyOFT).sendFrom{ value: msg.value }(
            address(this),
            destChainId,
            bytes32(uint(uint160(addr))),
            amount,
            callParams
        );
    }

    function claimAstr(address addr, uint256 _amount, uint256 _minAmount, bytes calldata _adapterParams) external payable {
        DerivativeContract(payable(derivativeAddress[addr])).withdrawAstr(_amount);
        ICommonOFT.LzCallParams memory callParams = ICommonOFT.LzCallParams(
            payable(_msgSender()),
            address(0),
            _adapterParams
        );
        IOFTWithFee(astrNativeOFT).sendFrom{ value: _amount + msg.value }(
            address(this),
            destChainId,
            bytes32(uint(uint160(addr))),
            _amount,
            _minAmount,
            callParams
        );
    }

    function setDerivativeAddress(address addr) internal {
        bytes memory bytecode = type(DerivativeContract).creationCode;
        bytes32 salt = bytes32(uint256(uint160(addr)));
        bytes memory deploymentData = abi.encodePacked(bytecode, salt);
        address contractAddress;
        assembly {
            contractAddress := create2(0, add(deploymentData, 32), mload(deploymentData), salt)
        }

        derivativeAddress[addr] = contractAddress;
    }

    function xcmTransferNativeAsset(address to, uint256 amount) internal {
        bytes32 publicKey = AddressToAccount.AddressToSubstrateAccount(to);
        address[] memory assetId = new address[](1);
        uint256[] memory assetAmount = new uint256[](1);
        assetId[0] = address(0);
        assetAmount[0] = amount;
        require(
            XCM(polkadotXcm).assets_reserve_transfer(
                assetId,
                assetAmount,
                publicKey,
                IS_RELAY_CHAIN,
                BIFROST_PARA_ID,
                0
            ),
            "Failed to send xcm"
        );
    }


    function xcmTransferAsset(address assetAddress, address to,  uint256 amount) internal {
        require(assetAddress != address(0), "Invalid assetAddress");
        bytes32 publicKey = AddressToAccount.AddressToSubstrateAccount(to);

        address[] memory assetId = new address[](1);
        uint256[] memory assetAmount = new uint256[](1);
        assetId[0] = assetAddress;
        assetAmount[0] = amount;
        require(
            XCM(polkadotXcm).assets_withdraw(
                assetId,
                assetAmount,
                publicKey,
                IS_RELAY_CHAIN,
                BIFROST_PARA_ID,
                0
            ),
            "Failed to send xcm"
        );
    }

    function getXtokensDestination(bytes32 publicKey) internal pure returns (XCM_v2.Multilocation memory) {
        bytes[] memory interior = new bytes[](2);
        interior[0] = bytes.concat(hex"00", bytes4(uint32(2030)));
        interior[1] = bytes.concat(hex"01", publicKey, hex"00");
        XCM_v2.Multilocation memory dest = XCM_v2.Multilocation({parents: 1,interior: interior});
        return dest;
    }

    receive() external payable {}
}

contract DerivativeContract is ReentrancyGuard  {
    address public astarReceiver;
    address public VASTR = 0xfffFffff00000000000000010000000000000010;

    constructor() {
        astarReceiver = msg.sender;
    }

    function balanceOf() public view returns (uint) {
        return IERC20(VASTR).balanceOf(address(this));
    }

    function withdrawVAstr() external nonReentrant returns(uint256) {
        require(msg.sender == astarReceiver, "DerivativeContract: forbidden");
        uint256 balance = balanceOf();
        require(balance != 0, "DerivativeContract: balance to low");
        IERC20(VASTR).transfer(astarReceiver, balance);
        return balance;
    }

    function withdrawAstr(uint256 _amount) external nonReentrant {
        require(msg.sender == astarReceiver, "DerivativeContract: forbidden");
        require(_amount != 0, "DerivativeContract: balance to low");
        (bool success, ) = msg.sender.call{value: _amount}("");
        require(success, "DerivativeContract: failed to withdrawAstr");
    }

    receive() external payable {}
}
","keccak256":"0x6bb9a7f978b693b45cb4fc3192bdb7dc2d1bfd69814de4e4fab6a6072ca500f1","license":"Apache-2.0"},"contracts/AstarSlpx.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.10;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "./interfaces/XCM.sol";
import "./interfaces/XCM_v2.sol";
import "./interfaces/ISlpx.sol";
import "./utils/BuildCallData.sol";
import "./utils/AddressToAccount.sol";

contract AstarSlpx is ISlpx, OwnableUpgradeable, PausableUpgradeable {
    address private constant NATIVE_ASSET_ADDRESS =
        0x0000000000000000000000000000000000000000;
    address private constant BNC_ADDRESS =
        0xfFffFffF00000000000000010000000000000007;
    address private constant XCM_ADDRESS =
        0x0000000000000000000000000000000000005004;
    bytes1 private constant ASTAR_CHAIN = 0x00;
    uint32 private constant BIFROST_PARA_ID = 2030;

    enum Operation {
        Mint,
        Redeem,
        ZenlinkSwap,
        StableSwap
    }

    struct AssetInfo {
        bytes2 currencyId;
        uint256 operationalMin;
    }

    struct FeeInfo {
        uint64 transactRequiredWeightAtMost;
        uint256 feeAmount;
    }

    mapping(address => AssetInfo) public addressToAssetInfo;
    mapping(Operation => FeeInfo) public operationToFeeInfo;

    function checkAssetIsExist(
        address assetAddress
    ) internal view returns (bytes2) {
        AssetInfo memory assetInfo = addressToAssetInfo[assetAddress];
        require(assetInfo.operationalMin > 0, "Asset is not exist");
        require(assetInfo.currencyId != bytes2(0), "Invalid asset");
        return assetInfo.currencyId;
    }

    function checkFeeInfo(
        Operation operation
    ) internal view returns (FeeInfo memory) {
        FeeInfo memory feeInfo = operationToFeeInfo[operation];
        require(
            feeInfo.transactRequiredWeightAtMost > 0,
            "Invalid transactRequiredWeightAtMost"
        );
        require(feeInfo.feeAmount > 0, "Invalid feeAmount");
        return feeInfo;
    }

    function initialize() public initializer {
        __Ownable_init();
        __Pausable_init();
        setAssetAddressInfo(NATIVE_ASSET_ADDRESS, 0x0803, 1000000000000000000);
        setOperationToFeeInfo(Operation.Mint, 10000000000, 1000000000000);
        setOperationToFeeInfo(Operation.Redeem, 10000000000, 1000000000000);
    }

    function setOperationToFeeInfo(
        Operation _operation,
        uint64 _transactRequiredWeightAtMost,
        uint256 _feeAmount
    ) public onlyOwner {
        require(
            _transactRequiredWeightAtMost <= 10000000000,
            "transactRequiredWeightAtMost too large"
        );
        require(_feeAmount <= 1000000000000, "feeAmount too large");
        operationToFeeInfo[_operation] = FeeInfo(
            _transactRequiredWeightAtMost,
            _feeAmount
        );
    }

    function setAssetAddressInfo(
        address assetAddress,
        bytes2 currencyId,
        uint256 minimumValue
    ) public onlyOwner {
        require(minimumValue != 0, "Invalid minimumValue");
        require(currencyId != bytes2(0), "Invalid currencyId");
        AssetInfo storage assetInfo = addressToAssetInfo[assetAddress];
        assetInfo.currencyId = currencyId;
        assetInfo.operationalMin = minimumValue;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function xcmTransferNativeAsset(uint256 amount) internal {
        require(
            amount >= addressToAssetInfo[NATIVE_ASSET_ADDRESS].operationalMin,
            "Less than MinimumValue"
        );
        bytes32 publicKey = AddressToAccount.AddressToSubstrateAccount(
            _msgSender()
        );
        address[] memory assetId = new address[](1);
        uint256[] memory assetAmount = new uint256[](1);
        assetId[0] = NATIVE_ASSET_ADDRESS;
        assetAmount[0] = amount;
        require(
            XCM(XCM_ADDRESS).assets_reserve_transfer(
                assetId,
                assetAmount,
                publicKey,
                false,
                BIFROST_PARA_ID,
                0
            ),
            "Failed to send xcm"
        );
    }

    function xcmTransferAsset(address assetAddress, uint256 amount) internal {
        require(assetAddress != address(0), "Invalid assetAddress");
        require(
            amount >= addressToAssetInfo[assetAddress].operationalMin,
            "Less than MinimumValue"
        );
        bytes32 publicKey = AddressToAccount.AddressToSubstrateAccount(
            _msgSender()
        );

        XCM_v2.Multilocation memory dest_account = getXtokensDestination(
            publicKey
        );
        IERC20 asset = IERC20(assetAddress);
        asset.transferFrom(_msgSender(), address(this), amount);
        require(
            XCM_v2(XCM_ADDRESS).transfer(
                assetAddress,
                amount,
                dest_account,
                XCM_v2.WeightV2(0,0)
            ),
            "Failed to send xcm"
        );
    }

    function mintVNativeAsset(
        address receiver,
        string memory remark
    ) external payable override whenNotPaused {
        require(bytes(remark).length <= 32, "remark too long");
        bytes2 nativeToken = checkAssetIsExist(NATIVE_ASSET_ADDRESS);

        xcmTransferNativeAsset(msg.value);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildMintCallBytes(
            _msgSender(),
            nativeToken,
            targetChain,
            remark
        );

        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.Mint);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Mint(
            _msgSender(),
            NATIVE_ASSET_ADDRESS,
            msg.value,
            receiver,
            callData,
            remark
        );
    }

    function mintVAsset(
        address assetAddress,
        uint256 amount,
        address receiver,
        string memory remark
    ) external override {
        require(bytes(remark).length <= 32, "remark too long");

        bytes2 token = checkAssetIsExist(assetAddress);

        // xtokens call
        xcmTransferAsset(assetAddress, amount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildMintCallBytes(
            _msgSender(),
            token,
            targetChain,
            remark
        );

        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.Mint);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Mint(
            _msgSender(),
            assetAddress,
            amount,
            receiver,
            callData,
            remark
        );
    }

    function redeemAsset(
        address vAssetAddress,
        uint256 amount,
        address receiver
    ) external override whenNotPaused {
        bytes2 vtoken = checkAssetIsExist(vAssetAddress);

        xcmTransferAsset(vAssetAddress, amount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildRedeemCallBytes(
            _msgSender(),
            vtoken,
            targetChain
        );
        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.Redeem);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Redeem(_msgSender(), vAssetAddress, amount, receiver, callData);
    }

    function stablePoolSwap(
        uint32,
        address,
        address,
        uint256,
        uint128,
        address
    ) external pure override {
        require(false, "Not support");
    }

    function swapAssetsForExactAssets(
        address assetInAddress,
        address assetOutAddress,
        uint256 assetInAmount,
        uint128 assetOutMin,
        address receiver
    ) external override whenNotPaused {
        bytes2 assetIn = checkAssetIsExist(assetInAddress);
        bytes2 assetOut = checkAssetIsExist(assetOutAddress);
        require(
            assetIn != bytes2(0) && assetOut != bytes2(0),
            "Invalid currencyId"
        );

        xcmTransferAsset(assetInAddress, assetInAmount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildSwapCallBytes(
            _msgSender(),
            assetIn,
            assetOut,
            assetOutMin,
            targetChain
        );
        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.ZenlinkSwap);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Swap(
            _msgSender(),
            assetInAddress,
            assetOutAddress,
            assetInAmount,
            assetOutMin,
            receiver,
            callData
        );
    }

    function swapAssetsForExactNativeAssets(
        address assetInAddress,
        uint256 assetInAmount,
        uint128 assetOutMin,
        address receiver
    ) external override whenNotPaused {
        bytes2 assetIn = checkAssetIsExist(assetInAddress);
        bytes2 nativeToken = checkAssetIsExist(NATIVE_ASSET_ADDRESS);

        xcmTransferAsset(assetInAddress, assetInAmount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildSwapCallBytes(
            _msgSender(),
            assetIn,
            nativeToken,
            assetOutMin,
            targetChain
        );
        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.ZenlinkSwap);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Swap(
            _msgSender(),
            assetInAddress,
            NATIVE_ASSET_ADDRESS,
            assetInAmount,
            assetOutMin,
            receiver,
            callData
        );
    }

    function swapNativeAssetsForExactAssets(
        address assetOutAddress,
        uint128 assetOutMin,
        address receiver
    ) external payable override whenNotPaused {
        bytes2 assetOut = checkAssetIsExist(assetOutAddress);
        bytes2 nativeToken = checkAssetIsExist(NATIVE_ASSET_ADDRESS);

        xcmTransferNativeAsset(msg.value);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildSwapCallBytes(
            _msgSender(),
            nativeToken,
            assetOut,
            assetOutMin,
            targetChain
        );
        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.ZenlinkSwap);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Swap(
            _msgSender(),
            NATIVE_ASSET_ADDRESS,
            assetOutAddress,
            msg.value,
            assetOutMin,
            receiver,
            callData
        );
    }

    function getXtokensDestination(
        bytes32 publicKey
    ) internal pure returns (XCM_v2.Multilocation memory) {
        bytes[] memory interior = new bytes[](2);
        // Parachain: 2001/2030
        interior[0] = bytes.concat(hex"00", bytes4(BIFROST_PARA_ID));
        // AccountId32: { id: public_key , network: any }
        interior[1] = bytes.concat(hex"01", publicKey, hex"00");
        XCM_v2.Multilocation memory dest = XCM_v2.Multilocation({
            parents: 1,
            interior: interior
        });

        return dest;
    }
}
