pragma solidity 0.8.10;

interface ISlpx {
    event Mint(
        address minter,
        address assetAddress,
        uint256 amount,
        address receiver,
        bytes callcode,
        string remark
    );
    event Redeem(
        address redeemer,
        address assetAddress,
        uint256 amount,
        address receiver,
        bytes callcode
    );
    event Swap(
        address swapper,
        address assetInAddress,
        address assetOutAddress,
        uint256 assetInAmount,
        uint128 assetOutMin,
        address receiver,
        bytes callcode
    );

    event StablePoolSwap(
        address swapper,
        uint32 poolId,
        address assetInAddress,
        address assetOutAddress,
        uint256 assetInAmount,
        uint128 minDy,
        address receiver,
        bytes callcode
    );

    /// Minted vNative assets such as vASTR, vGLMR, vMOVR
    function mintVNativeAsset(
        address receiver,
        string memory remark
    ) external payable;

    /// Minted vAssets
    function mintVAsset(
        address assetAddress,
        uint256 amount,
        address receiver,
        string memory remark
    ) external;

    /// Redeem assets
    function redeemAsset(
        address vAssetAddress,
        uint256 amount,
        address receiver
    ) external;

    /// Swap one asset for another
    function swapAssetsForExactAssets(
        address assetInAddress,
        address assetOutAddress,
        uint256 assetInAmount,
        uint128 assetOutMin,
        address receiver
    ) external;

    /// Swap one asset for native asset
    function swapAssetsForExactNativeAssets(
        address assetInAddress,
        uint256 assetInAmount,
        uint128 assetOutMin,
        address receiver
    ) external;

    /// Swap native asset for another
    function swapNativeAssetsForExactAssets(
        address assetOutAddress,
        uint128 assetOutMin,
        address receiver
    ) external payable;

    function stablePoolSwap(
        uint32 poolId,
        address assetInAddress,
        address assetOutAddress,
        uint256 assetInAmount,
        uint128 minDy,
        address receiver
    ) external;
}
